waves.frame_eqs_eel:
vol = bass*8 + mid*5 + treb*3;
m = m*0.97 + vol*0.08;
monitor = vol;
beat = above(vol,res)*above(vol,m)*above(vol,16);
diff = (1-beat)*diff + beat*(vol-res);
res = beat*(vol + m*0.04) + (1-beat)*(res -  (0.1+diff*0.02)*60/fps);
res = max(0,res);
b = beat;
g = 1-beat;

init_eqs_eel:
n = 0;
loop (100000,megabuf(n)=0;gmegabuf(n)=0;n += 1;);

posx = rand(10); posy = rand(10);
ranx = rand (1); rany = rand(1);
ranh = rand(10);
t0 = time;
st = 0.5; st_=0.5;

frame_eqs_eel:
st = max (0, st-0.2/fps);
dec = 1-0.5/fps;
st_ = st_* (dec) + (1-dec)*st;

//dir
dir = 0.7*sin(time*.0162+rany*6);
q2 = dir+0.5; 
q3 = sin(dir);
q4 = cos(dir);

//scaling plane
q7 = 0.002;

//movement
posx = time*0.0003;
posy = time*0.004;
q10 = posx;
q11 = posy;

//start ran for warp mod 
q15 = ranx;
q16 = rany;

test = (t0-time)*0.047-3.14/2;
view = 0.7 + min(1, max (-1, 2*sin(test)))*0.17;

//Alti
q8 = 4 + 3* sin(test) + (view-0.55)*16;

//hory (Kippung)
q6 = view * (1-st_) + st_*2;

//q6  = 0.55;
monitor = view;

warp:
#define sat saturate
const float quality = 4;
vec3 rsl, rsl3, ret1, sky, mod;
vec2 dz, uv0, uv1, uv2, uv3, uv4, uvc, dz1;
float  lav_gnd,lav_gnd2, smask, ripple, l,d,i,p;
const float hory = q6, xyscale = q7, alti = q8;
const vec2 hor = texsize.zw*vec2 (1.5,0), dir = vec2 (q3, q4), modran = vec2 (q15,q16);
const vec3 mov = vec3 (q10,q11,0);
float comb (vec2 uvi) {return dot (texture(sampler_noise_lq, uvi).yx, vec2(1,1));}

vec2 noise2 (vec2 uvi) {vec2 tmp = vec2(1); float s = 1;
  for (int n = 1; n <= 2; n +=1 ) {tmp *= 1-texture(sampler_noise_lq,uvi*s).xyz; s*= 3;}
  return tmp;}


vec2 noise3 (vec3 uvi, vec3 mod) {uvi *= 0.16; vec2 tmp = vec2(1); float s = 1; float diff = 0;
  uvi.xy+= modran*3;
  for (int n = 1; n <= 2; n +=1 ) {tmp *= sat(1-texture(sampler_noise_lq,uvi*s).xyz*(0.8+mod.y/2)/s); s*= 3+tmp*0.002*mod.z;}
  for (int n = 1; n <= 3; n +=1 ) {tmp *= sat(1-texture(sampler_noise_lq,uvi*s).xyz*(0.8+mod.y/2)/s); s*= 3;}
  diff = sat (16-2*uvi.z-20000* abs (tmp.x-0.4)) ;
  tmp  -= diff*0.009 ;
  return 1-pow(tmp,0.8+mod.x);}

vec2 enoise3 (vec2 uvi, vec3 mod) {uvi/=6; vec2 tmp = vec2(1); float s = 1;
  for (int n = 1; n <= 6; n +=1 ) {tmp *= 1-texture(sampler_noise_lq,uvi*s).xyz/(0.8+mod.y)/s; s*= 2.9;}
  if ((tmp.x > 0.398) && (tmp.x < 0.402)) {tmp = 0.398;}
  return 1-pow(tmp,.8+2*mod.x);}

vec3 rs_lav (vec2 uvi, float berg) {  float z; vec2 rs;
  z = 1/(uvi.y+hory)*(alti-berg*16); //z = clamp(z,0,4/xyscale);
  rs.x = uvi.x * 2*z ; rs.y = z; 
  return vec3(rs*xyscale,z); }


shader_body {
uv1=uv-0.5;
lav_gnd=  -min (texture(sampler_fc_main,uv).b, texture(sampler_fc_main,uv+hor.yx).b);

rsl = rs_lav(uv1,lav_gnd)+mov; 
mod = texture(sampler_noise_mq,rsl.xy*.043+modran).xyz;

i=0, p=0;
for (int n=1; n <= quality; n++) {
  p =  ((lav_gnd+noise3 (rsl, mod))/quality);
  i += p;
  lav_gnd -= p*0.1+i*0.2; 
  rsl = rs_lav(uv1,lav_gnd)+mov; 
}
l = clamp(-lav_gnd, 0.0, 1.0);

d=1.8;
dz = -1.5/rsl.z*xyscale*d*dir;

rsl3 = rsl * 8 + l*0;
ripple = ((noise2 (rsl3-dz) - noise2 (rsl3+dz))* (1-l))*1+0.02;

ripple = (1/rsl.z*ripple+noise3 (rsl-vec3 (dz,0),mod) - noise3 (rsl+vec3 (dz,0),mod))*(1-l)/d/sqrt(50+rsl.z)*2000;

ripple = 1-exp(-ripple);

ret.r = ripple;
ret.b = l;
ret =mix(ret,((texture(sampler_blur1, uv).xyz * scale1) + bias1),0.1);
ret.g = rsl.z*xyscale*4;
}

comp:
shader_body
{
    vec2 d = texsize.zw*4;
    vec3 dx = ( ((texture(sampler_blur1, uv+vec2(1,0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    vec3 dy = ( ((texture(sampler_blur1, uv+vec2(0,1)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );
vec2 uv_bg = uv - vec2(dx.y,dy.y)*texsize.zw*16;
ret =(((texture(sampler_blur2, uv_bg).xyz * scale2) + bias2)-((texture(sampler_blur1, uv_bg).xyz * scale1) + bias1)*1+texture(sampler_main, uv_bg).xyz*0.9 + (-dx.y + dy.y)*0.4).y*0.8*vec3(1,1.1,1.2);

