comp:
vec2 gradBlur1( vec2 domain, vec2 d, vec3 filter){
   vec3 dx = ( 2*((texture(sampler_blur1, domain + vec2(1,0)*d).xyz * scale1) + bias1) - 2*((texture(sampler_blur1, domain-vec2(1,0)*d).xyz * scale1) + bias1) );
   vec3 dy = ( 2*((texture(sampler_blur1, domain + vec2(0,1)*d).xyz * scale1) + bias1) - 2*((texture(sampler_blur1, domain-vec2(0,1)*d).xyz * scale1) + bias1) );

   return 0.5*vec2(dx.x*filter.x + dx.y*filter.y + dx.z*filter.z,
                     dy.x*filter.x + dy.y*filter.y + dy.z*filter.z)/(filter.x+filter.y+filter.z);
}

shader_body
{
vec2 d = texsize.zw*4;
vec2 grady = gradBlur1(uv, d, vec3(0,1,0));
vec2 gradx = gradBlur1(uv + grady*d, d, vec3(1,0,0));
vec2 uv_green = uv-gradx*d*8;
ret = (0.7 + (grady.x+grady.y))*texture(sampler_main, uv_green-grady*texsize.zw*8).xyz.y;
vec3 c1 = mix(0,vec3(1,1,1),texture(sampler_main, uv - gradx*d*0).xyz.x)*clamp(1-texture(sampler_main, uv).xyz.y*2, 0.0, 1.0);
ret = mix(ret,vec3(1,1.4,2.4), (((texture(sampler_blur3, clamp(uv_green + grady*texsize.zw*8, 0.0, 1.0)).xyz * scale3) + bias3).y-1.3*texture(sampler_main, uv_green-grady*texsize.zw*8).xyz.y));
vec4 noise9 = texture(sampler_noisevol_hq, 
                      ((uv_green.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1,1,0)*0.05 + 
                      time*vec3(0,0,1)*q31
                     ).xyz;
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
ret =  mix(c1,1,ret);
ret = mix(ret,vec3(0,0,0),clamp(1*texture(sampler_main, uv+grady*d*4 - gradx*d*16).xyz.z, 0.0, 1.0));
ret /= vec3(-2.0,0.4,0.5);
}

