tuv.x -= pow(nshape,0.2)*6*srad/tubes*tuv0.x;
mod = tex2D(sampler_pw_noise_lq,tuv.x*0.0002+rand_preset.z);
mod2= tex2D(sampler_pw_noise_lq,tuv.x*0.00007+rand_preset.y);

k1 = tuv.x*6*mod.x;
k2 = tuv.y*2*M_PI*tubes/2-4.7;
skym  = sat(sin(k2))*(sin(k1)+mod.y)*2;
skym1 = sat(sin(k2)+.4)*(sin(k1+frac(q19/8)*2*M_PI)+mod.y)*2 ;
skym1 = skym1 > .1+mod2.z;

k1 = abs(sin(tuv.x/tlen*M_PI*2*int(1+mod2.x*3.1)));
nshape *= sat(1.5*k1); 
istube = sat (20*nshape-2); 

float2 sc = float2(0.005,0.25*mod.z);
uv5  = tuv*float2(0.25/tlen,tubes/16.0); 
tuv += 0.25*int(8*skym)*!istube;
skym = sat (1+8*mod2.z-8*skym);
noise = near*!istube* (0.1+tex2D(sampler_pw_noise_lq,tuv*sc)-tex2D(sampler_pw_noise_lq,(tuv+0.003)*sc)*0.75).r;
col = (tex2D(sampler_pw_noise_lq,uv5/32)+0.1);

nshape2 = sat(q2+2*abs(sin(mod2.z*12*tan(ftu/srad)+q1*cos(q3*int(8*uv5.x)))));
neon = (0.3+nshape)*nshape2*istube*col;
neons= sat(k1)* saturate(1-8/tubes*abs(ftu)) *col;

float dir = (int(uv5.y*16)%tubes)/2;
nmask = frac(((q4*uv5.x*8.0) *q20-q21)) < q22;
nmask *=  sat(q26*(tria((uv5.y*16.0+1)/q25+q27*2)-1)+1) ; 

ret = (neon + neons*noise)* nmask*near*1.1;
uv2 = (uvw-0.5)*0.15+0.5;
lg = GetBlur1(frac (uv2))*float3(1,1,0);
lg += lum(lg)+0.1;
ret += noise* lg*q11*near*!istube;

float3 back = pow(uv.x-0.2,3)*bcol+GetPixel(uvw).b*wmask*ocol;
ret = lerp (ret, back, sat(1-10*length(uv1))) *skym;

ret += (GetPixel(uvw)+2*GetBlur2(uvw))*wmask*float3(0.8,0.8,0);
stars = 0;
int n = 1, sanz = 4;
float t0 = q19*0.005;
while (n <= sanz) {
  float arg = (1.0*n/sanz-t0); 
  float dist = frac(arg);
  float inten =  sat(1-dist); 
  float2 uv4 = 4*64 * dist* uv1;
  float2 snoise = tex2D (sampler_pw_noise_lq,uv4/256);
  float smask = sat(1-snoise.x)*100+2;
  stars += (snoise.x>0.97)*inten* sat(1-2*pow(smask*length(frac(uv4)-0.5),2));
  stars += inten*sat(tex2D(sampler_noise_hq,uv4*0.002).r-0.8)*bcol;
n++;
}
ret = (ret)+(stars+back)*sat(1-4*skym*near);
//li =  sat (1.2-1*abs(-uv.y+0.5)-2*uv.x);
li = sat (1-2*abs(tuv0.y));
ret += li*near*noise*skym*skym1*(bcol+0.25) + nshape*0.05;

uv2 = frac (tuv0*float2(0.003,tubes/2)+float2(.51,0));
ret += GetBlur1(uv2)*float3(1,1,0)*istube/2;
uv2 = frac (tuv0*float2(0.05,3.5+0*tubes/2)+float2(.15,0));
ret += GetBlur1(uv2).b*istube*ocol/4*sat (1-3*li);

ret += q12*exp(-6*length(0.5*tria(2*q10+tuv0.x*0+tuv0.y+tex2D(snh,tuv0.y)))) * (istube/4+!skym) * float3 (0.25,0,0.5) ;

ret += uv.x*0.02/length(frac(1.5*uv1-float2(rand_preset.xy/2))-0.5)*!skym*bcol;
