init_eqs_eel:
n = 1500; loop (15000,megabuf(n) =0;gmegabuf(n) = 10; n+=1;);
flen = 128; shapes = 16; reg01 = shapes;
minbpm=300; maxbpm=500; num_res=200;
slow = 1; r1=8; r2=1; run = 0; bvol_=0; dir = 1; tlen=2.5; ndir = 1; size_=1; vol_=1; certain = 1; movarg = rand(100);
lc = rand(2)-1; tr1 = rand(5); tr2 = rand(5); r5 = rand(6); r9 = rand(1); vc_=rand(10); r3=2; tubes = pow(2,1+int(rand(3)));
start = time; ray_=0;

frame_eqs_eel:
dec_f = 1-10/fps; dec_m = 1-3/fps; dec_s = 1-1/fps; dt=1/fps;

maxind = 0; qavg = 0.1; i=0; 
bvol = max(0.1,(bass+mid+treb)/3); bvol_=bvol_*dec_m + (1-dec_m)*bvol;
Excite = (bvol-bvol_);
loop (num_res,
  n = i*8;
  Freq  = (i/(num_res-1)+minbpm/(maxbpm-minbpm))*(maxbpm-minbpm)/60;
  Feder = sqr(Freq*6.2832);
  megabuf (n+4) = megabuf(n+1); megabuf (n+1) *= 1-0.25*dt; 
  EC_Steps = int (8*Freq*dt)+1;
  loop (EC_Steps, // Euler-Cauchy steps
    Kraft = Excite - Feder*megabuf(n + 2) ;
    megabuf (n+1) += Kraft        * dt/EC_Steps;
    megabuf (n+2) += megabuf(n+1) * dt/EC_Steps;  );
  beatcos = megabuf(n+1);  beatsin = megabuf(n+2)*Freq*6.2832;
  quad = sqrt (pow(beatsin,2) + pow(beatcos,2));
  megabuf (n+3) = megabuf(n+3) *dec_m +  quad* (1-dec_m);
  if (megabuf(n+3) > megabuf (maxind*8+3), maxind =i,0);
  qavg = qavg+pow (megabuf(n+3),2);
  i += 1;);

qavg = sqrt (qavg/num_res); 
qdev = 0; i =0; loop (num_res, qdev += pow(megabuf(i+3)-qavg,2); i += 8; );
qdev = sqrt (qdev/num_res);
BPM =   (maxind/(num_res-1)+minbpm/(maxbpm-minbpm))*(maxbpm-minbpm);
if (BPM > 200, BPM *= 0.5, 0);

m1 = maxind*8;
certain = certain * dec_m + (1-dec_m) * megabuf(m1+3)/qavg;
dbl_beat = megabuf(m1+1) * megabuf (m1+4) < 0;
sgl_beat = dbl_beat * (megabuf (m1+4) < 0);
bcount += dbl_beat;is_rbeat  = dbl_beat && (bcount%2==0);
bcount2 += is_rbeat;
trel1 += BPM/60*dt/2;

n = 0; vol = .0; bbase = 10000;
loop (flen,
  value = gmegabuf(n)/vol_;
  trig = value > megabuf(bbase+n)*1.5*30/fps; 
  megabuf (bbase+n) = value;
  dec = if(trig,dec_f,dec_m); 
  megabuf(n+5000) = megabuf(n+5000)*dec + (1-dec)*value;
  vol = vol + pow(megabuf(n+5000),2);
  n += 1;
);

vol = sqrt(vol);
vol_ = dec_s*vol_ + (1-dec_s)*vol*7;

// post smoothing
n = 0;
loop (flen,
  value = megabuf(n+5000) * 5*n/(1+5*n);
  dec = if (megabuf(4000+n)<=value,.9,.9);
  dec = dec_f; 
  megabuf(n+4000) = megabuf(n+4000)*dec+ (1-dec)*value;
  n = n+1;
);


// Berechnung Ortskoordinaten aus Spektrum
step = flen/shapes/2+1; 
n = 2000; m = 4000+step; k=0; slope = 0.01; speed = 0.4/slope; size = 0;
loop (shapes,
   //rad, ang slope limitation
   x0 = megabuf(m)-megabuf(m+1);
   y0 = megabuf(m)-megabuf(m-1);
   radi = sqrt (x0*x0+y0*y0);
   angi = atan2 (x0,y0);
   j=n+4000;
   megabuf(j)   +=min(slope, max (-slope, radi-megabuf(j)))*speed/fps; 
   megabuf(j+1) +=min(10*slope, max (-10*slope, angi-megabuf(j+1)))*speed/fps; 
   megabuf(n)   = megabuf(j)*cos(megabuf(j+1));
   megabuf(n+1) = megabuf(j)*sin(megabuf(j+1));
  size += pow (megabuf(n),2)+pow (megabuf(n+1),2);
  dec = if (bvol2 > bvol,dec_m, dec_m);
  bvol2 = dec*bvol2 + (1-dec)*bvol;
  megabuf(n+2) = 0.04/sqrt(pow(megabuf(n),2) + pow (megabuf(n+1),2));
  m = m + step; n = n+8; k= k+1;
);

//Projektion
n = 1000; m = 2000;
size_ = size*sec_s + (1-dec_s)*sqrt(size)*shapes*7;
loop (shapes,
  gmegabuf(n) = megabuf(m)/megabuf(m+2)/size_;
  gmegabuf(n+1) = megabuf(m+1)/megabuf(m+2)/size_;
  gmegabuf(n+2) = megabuf(m+2);
  n = n+8; m = m+8;
);

reg13 = 1.7+0.25*sqrt(mid_att+bass_att); //shp rad

trig = (bcount%64 == 0) && dbl_beat;
if (trig, 
  r1 = 4+int (rand(8));
  r2 = max (2,int ((r1-2)*rand(1)));
  r3 = -1*pow (2, int(rand(2)));
  s1 = pow(2,int(rand(4)));
  s2 = sqrt(rand(1.5))*s1;
  if (rand(1)>0.5,s1=-s1,0);
  s3 = -1*pow (2, int(rand(4)));
  ndir = if (rand(1)<0.2,-1,1);
,0);

run = if (bvol_<0.4,0,if(bvol_>0.7,1,run));
slow = if (certain<1.7,1,if(certain>2.5,0,slow));

dir_ = dir_*dec_f+dir*5*(1-dec_f);
q4 = ndir;
movarg = min(4,(2+certain*2)/3.5)*dir_;
movst  += (5+ movarg)*0.02*dt;
movarg *= dt;
reg60 = movst;
tmove += movarg;
q19=tmove/2;

//mask shift x
q20 = 1/r1;  //Anteil
q22 = q20*r2; //Limit
trelx += BPM/120/q20*run*dt* (1-0.5*slow) * r3 + ndir*movarg*run; //ungef. Bew. Korr
q21 = q20*int(trelx); //phase

//mask shift y
q25 = s1;  //Divisor, 1,2,3,4,6 bei 12 tubes
q26 = s2;  //Narrow
trely += BPM/120/3*2*q25*s3*run*dt* (1-0.5*slow);
q27 = int(trely)/q25;  //phase

q10 = trel1;
trig = (bcount%4==0) && dbl_beat;
tbers = max(0,if (bers, tbers+dt, tbers-dt/2));
tber2 = max(0,if (ber2, tber2+dt, tber2-dt/2));
vc = certain+bvol*0.25;
vc_=dec_s*vc_ + (1-dec_s)*max(0,certain-2);
if (vc > 5.9+tbers, bers=1, if (vc < 4.5+tbers/5, bers=0;,0));
if (vc > 4.5+tber2, ber2=1, if (vc < 3+tber2/5, ber2=0;,0));

q5 = sqrt(tubes)*0.025*r9+0.005;
q6 = tubes;
q7 = tlen;
q17=ddx; q18 = ddy; 
ber2_ = ber2_*dec_s + (1-dec_s)*ber2;
reg10 = min(1,pow (certain/5,2)) * ber2_; //flash

reg32 = aspecty;
q31 = reg51;

trig = (bcount%50==0) && dbl_beat;
if (trig,lc=rand(2)-1.2; lrot=rand(vc_*5)-vc_*2; lve=rand(6); ray=(rand(60)<vc+30*ray), 0);
ray_ = dec_s*ray_+(1-dec_s)*ray;
q2 = lc;
q12 = ray_*1.2;;

q1=lrot*q19*2; 
q2=lc;
q3=lve;

tr1 += vc_*dt*0.05;
tr2 += vc_*pow(sin(tr1),1)*0.5*dt;
q8 = tr2;
ramp = 0.5*min(1,vc_*0.25)*pow(cos(tr2*(0.5*sin(r5+time*0.053))),5);
q9 = ramp+0.95;
q11 = (1+mid_att/2)*.7;
q15 = bvol_;
rot = 0.0; zoom = 1+movarg*0.5; warp = 0; dx = 0; dy = 0;
monitor = bpm;

warp:
// by LamersAss, V1 (28/5/2022)

shader_body
{
  vec3 _fx_vol = vec3( bass_att, mid_att, treb_att )/6;

  for ( int i = 0; i < 3; i++ )
  {
    _fx_vol[i] = ( min( 1., _fx_vol[i] ) * 1.7 )
                 + max( .0, _fx_vol[i] - 1. );
    _fx_vol[i] = min( 2.46, _fx_vol[i] );
  }

  _fx_vol += rot_f3[0] * .1;
  _fx_vol *= .07;
  _fx_vol *= 1.9;
  _fx_vol += 0.022;

  float _fx_decay = fps * 0.015786;
  float _fx_rad = length( vec2( 
    ( uv.x - .5 ) * aspect.x, 
    ( uv.y - .5 ) * aspect.y ) );
  float _fx_zoom = max( 0, _fx_rad - .12 ) * .028;

  _fx_zoom *= 1. + max( 0, ( cos( ang * 4 ) * .7 ) - .4 );

  vec2 _fx_prevframe = uv_orig;
  _fx_prevframe.x += ( _fx_zoom * aspect.y ) * ( uv_orig.x > .5 ? -1. : 1. );
  _fx_prevframe.y += ( _fx_zoom * aspect.x ) * ( uv_orig.y > .5 ? -1. : 1. );

  ret = texture( sampler_main, _fx_prevframe ).xyz * _fx_decay;

  float _fx_width = ( _fx_rad * .04 ) + ( vol * .004 );
  float _fx_spinmult = 600.;

  for ( int i = 0; i < 3; i++ )
    if ( _fx_rad > ( _fx_vol[i] - _fx_width ) 
      && _fx_rad < ( _fx_vol[i] + _fx_width ) )
      ret[i] = max( ret[i],
        ( 12. * _fx_vol[i] ) *
        + ( 12. * cos( ang + ( _fx_spinmult * _fx_vol[i] ) ) )
     );

  vec3 _fx_glow_intens = vec3(-2.7,0.3,2.5);
  vec3 _fx_glow_vol = ( _fx_vol * 0.92 ) * max( 0, _fx_rad - .2 );

  for ( int i = 0; i < 3; i++ )
    ret[i] = max( ret[i], _fx_glow_vol[i] * _fx_glow_intens[i] );
}

