uniform sampler2D sampler_fw_clouds;
shader_body
{// sample previous frame
ret =texture( sampler_main, uv ).xyz;
float blur =sin(bass*90)*rad;
ret=0.125*(
texture(sampler_main, vec2(uv.x-0.01*blur,uv.y-0.01*blur)).xyz +
texture(sampler_main, vec2(uv.x+0.01*blur,uv.y+0.01*blur)).xyz +
texture(sampler_main, vec2(uv.x-0.01*blur,uv.y+0.01*blur)).xyz +
texture(sampler_main, vec2(uv.x+0.01*blur,uv.y-0.01*blur)).xyz +
texture(sampler_main, vec2(uv.x,uv.y-0.01*blur)).xyz +
texture(sampler_main, vec2(uv.x,uv.y+0.01*blur)).xyz +
texture(sampler_main, vec2(uv.x-0.01*blur,uv.y)).xyz +
texture(sampler_main, vec2(uv.x+0.01*blur,uv.y)).xyz);
//add a 'bass overbright' effect:
ret-=-(lum(ret)<sin(time*90))*ret*blur+(lum(ret)>sin(-time*90))*ret*blur;
}

comp:
float d;
vec3 emboss, dx, dy;
shader_body
{

    d = texsize.zw*1;
    dx = ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz )*8;
    d = texsize.zw*2;
    dx += ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz )*4;
    d = texsize.zw*3;
    dx += ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz )*2;
    d = texsize.zw*4;
    dx += ( texture(sampler_main, uv+vec2(1,0)*d).xyz-texture(sampler_main, uv-vec2(1,0)*d).xyz );


vec3 wires = (dx.x*0.15 + 0.5)*((texture(sampler_blur3, uv).xyz * scale3) + bias3).x*mix(vec3(2.3,0.4,2.7),vec3(2.4,0.5,2.5),((texture(sampler_blur2, uv).xyz * scale2) + bias2).z*2);
ret =mix(texture(sampler_main, uv).xyz.z*vec3(0.8,0.1,0.5),1,wires);

    d = texsize.zw * 8;
    dx = ( ((texture(sampler_blur1, uv + vec2(1,0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(1,0)*d).xyz * scale1) + bias1) );
    dy = ( ((texture(sampler_blur1, uv + vec2(0,1)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv-vec2(0,1)*d).xyz * scale1) + bias1) );

uv = uv - vec2(dx.x,dy.x)*0.08;


ret =mix(ret,vec3(1.5,0,1.6)-0.2,((texture(sampler_blur2, uv).xyz * scale2) + bias2).y);
ret =mix(ret,vec3(0.1,0.3,0.8)+1,texture(sampler_main, uv).xyz.y*1.2);

//MilkDrop3 Color Mode:
ret /= vec3(-2.1,0.7,2.6);
//MilkDrop3 Burn Mode:
ret =mix(ret,2,texture(sampler_main, uv).xyz.z*1.36);
}

