frame_eqs_eel:
dec_med = pow (0.8, 30/fps);
dec_slow = pow (0.95, 30/fps);
//dec_xlow = pow (0.999, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .2+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %32;
index2 = (index2 + is_beat*bnot(index))%4;

q22 = peak;
q24 = is_beat;
q26 = bass_att + mid_att + treb_att;
vol = vol*dec_med + (1-dec_med)*q26;
q27 = index + 1;
q28 = index2 +1;

p2 = .3*sin(time) + .3*cos(time/3);
//*****tunnel tilting
q1 = cos(p2 * 3.1416/4);
q2 = sin(p2 * 3.1416/4);
q3 = -q2;
q4 = q1;

//***tunnel width
q21 = (1+sin(time/12))/8+.01;

//**floor proximity
prox = prox - above(vy,.3)*prox*2;
q32 = .5 + .32*prox;
monitor = prox;

//****tunnel rotation
rota = rota +.01*below(index%16,8)/fps*30;
rota1 = rota1*dec_med + (1-dec_med)*rota;
q7 = rota1/2;

//****tunnel speed
movez = movez + 1.2/(1+abs(vx)+abs(vy))/fps;
q8 = movez * (1+q21/4);
//dyn. Aenderung von q21 bewirkt auch Richt.wechsel

//***********************
trig1= q24*bnot(index%12);
trig2= q24*bnot((index+4)%12);
trig3= q24*bnot((index+8)%12);
trel = trel + vol/fps;
z0 = 1;
speed = vol/2;
hx = sin(trel);
hy = cos(trel);

p1z = bnot(trig1)* (p1z + (5+p1z)*speed/fps)+trig1*z0;
p1z = max(p1z,.1);
q9 = hx/(p1z); 
q10 = hy/(p1z); 
q11 = p1z;
q12 = .1+.02*q26*bnot((index+2)%3)*p1z*exp(-p1z/100);

p2z = bnot(trig2)* (p2z + (5+p2z)*speed/fps)+trig2*z0;
p2z = max(p2z,.1);
q13 = 2*hx/(p2z); 
q14 = -.5*hy/(p2z); 
q15 = p2z;
q16 = .1+.02*q26*bnot((index+2)%3)*p2z*exp(-p2z/100);


p3z = bnot(trig3)* (p3z + (5+p3z)*speed/fps)+trig3*z0;
p3z = max(p3z,.1);
q17 = -1.4*hx/(p3z); 
q18 = 2*hy/(p3z); 
q19 = p3z;
q20 = .1+.02*q26*bnot((index+2)%3)*p3z*exp(-p3z/100);

