comp:
uniform sampler2D sampler_pw_noise_lq;

float k1, k2, mask1, mask2, mask3, rad1, rad2, rad3;
vec2 rs, rss, rss0, rs0, uv2, uv3,uv4, rsk, uvo;
vec2 dz1, dz2, dz3, Kugel1, Kugel2, Kugel3,tmp;
vec3 noise, ret1, ret2;

shader_body {
uvo = uv;
uv = (mat2(_qa) * uv*aspect.xy);

uv = uv + -.0- .1*vec2(q5,q6);
vec2 uv1 = (uv-.5 );//*aspect.xy;

//Kugel1
rsk = (uv1 + vec2 (q9,q10)) ;
dz1 = normalize(rsk);
rad1 = q11*length (rsk) ;
uv4 =  tan(rad1)*dz1;
mask1 = clamp(8-8*rad1, 0.0, 1.0);
Kugel1 = uv4*mask1;

//Kugel2
rsk = (uv1 + vec2 (q13,q14)) ;
dz2 = normalize(rsk);
rad2 = q15*length (rsk) ;
uv4 =  tan(rad2)*dz2;
mask2 = clamp(8-8*rad2, 0.0, 1.0);
Kugel2 = uv4*mask2;

//Kugel3
rsk = (uv1 + vec2 (q17,q18)) ;
dz3 = normalize(rsk);
rad3 = q19*length (rsk) ;
uv4 =  tan(rad3)*dz3;
mask3 = clamp(8-8*rad3, 0.0, 1.0);
Kugel3 = uv4*mask3;

vec2 dz = (Kugel2 + Kugel1 + Kugel3)*.2;
//******************** STARS
uv1 += dz;
rss0.x = atan(uv1.x, uv1.y)/3.1416;
rss0.y = .1*q21/(length (uv1));

rss = vec2 (rss0.x,rss0.y+time);
rss = (mat2(.7,.7,-.7,.7) * rss);
float noise = (texture(sampler_pw_noise_lq,rss/32).xyz-.5 >= 0);
tmp = abs(fract(rss*8)-.5);
vec3 dots = clamp(.04/length(tmp), 0.0, 1.0) *noise;

rss = vec2 (rss0.x,rss0.y+time/4);
rss = (mat2(.7,.7,-.7,.7) * rss);
noise = (texture(sampler_pw_noise_lq,rss/32).xyz-.5 >= 0);
tmp = abs(fract(rss*8)-.5);
dots += clamp(.02/length(tmp), 0.0, 1.0) * noise;

dots *= clamp(.2/abs(rss0.y), 0.0, 1.0);
dots *= dots*.6;
//*************************

float z = q32/(uv1.y-.02);//floor proxy
float mask = clamp(4/z, 0.0, 1.0);
rs0.x = (uv1.x)*z;
rs0.y = z/4;
rs.x = rs0.x ;
rs.y = rs0.y + q8*3;
vec3 mirror = lum(texture(sampler_pw_noise_lq,.02*rs).xyz)-.5;
z = .2/(length(uv1)-q21);
uv2.y = z + q8;
uv2.x = rss0.x +q7;
uv3 =fract(uv2 +.0*mask*mirror);

ret1 =  texture (sampler_main,uv3).xyz/z;
ret = max(ret1*(1-0.5*mask),mirror*mask) +dots;

float mod = q22*2;
vec3 blk = abs(inversesqrt(rad1)*dz1.x*roam_sin
+inversesqrt(rad2)*dz2.y*roam_sin.rbg
+inversesqrt(rad3)*dz3.x*roam_sin.bgr)*(.2+mirror*mask)*mod; 
ret += blk;

ret2 = clamp(.02*q22*q21/length (uv1)*
 (lum(texture (sampler_main,uv1+.5).xyz)), 0.0, 1.0);
ret2 /= (length(uv1)+.02);
float k1 = lum(ret2);
ret += k1*vec3(1,k1,k1/2)  ;
}

