frame_eqs_eel:
decay = .9;
warp = 0;

bassc = if(above(bass,bassc),bassc + bass*.07,bassc-.03);
midc = if(above(mid,midc),midc + mid*.07,midc-.03);

q1 = bassc/32;
q2 = midc/32;

warp:
shader_body
{
    uv = uv;                       // 0..1 across the viewport
    uv.x = fract(9.0 * sin(8.0 * (ceil(uv.x * 384.0) / 128.0)));

    float t = time/3 * ((uv.x + 0.5) / 2.0);
    float b = pow(1.0 - fract(uv.y + uv.x + t), 4.0);

    b = b* q1+ step(1.0 - b, 0.01);

    ret = vec4(b * vec3(0.0, 0.5, 1.0), 1.0);
}

comp:
shader_body
{

ret = mix(((texture(sampler_blur2, uv).xyz * scale2) + bias2),texture(sampler_main, uv).xyz,0.44);

ret = (ret.y-ret.x*0.5)*mix(vec3(2.4,2.7,2.8),1,ret.x)*2.4;
ret*=1.2;
//MilkDrop3 Burn Mode:
ret = mix(ret,2,texture(sampler_main, uv).xyz.z*1.36);
}

warp_hlsl:
shader_body
{
    uv = uv;                       // 0..1 across the viewport
    uv.x = frac(9.0 * sin(8.0 * (ceil(uv.x * 384.0) / 128.0)));

    float t = time/3 * ((uv.x + 0.5) / 2.0);
    float b = pow(1.0 - frac(uv.y + uv.x + t), 4.0);

    b = b* q1+ step(1.0 - b, 0.01);

    ret = float4(b * float3(0.0, 0.5, 1.0), 1.0);
}

comp_hlsl:
shader_body
{

ret = lerp(GetBlur2(uv),GetPixel(uv),0.44);

ret = (ret.y-ret.x*0.5)*lerp(float3(2.4,2.7,2.8),1,ret.x)*2.4;
ret*=1.2;
//MilkDrop3 Burn Mode:
ret = lerp(ret,2,GetPixel(uv).z*1.36);
}
