warp:
shader_body
{
vec2 pixelsize = texsize.zw;
vec2 uvm = uv_orig;

vec2 noise_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
vec3 noiseVal = texture(sampler_noise_lq, noise_uv).xyz;

ret.xyz = clamp(texture( sampler_pw_main,uvm).xyz*vec3(1,1,1) - vec3(.0,.0,.0), 0.0, 1.0);
ret.xyz = texture( sampler_pw_main,uvm).xyz;


vec3 rsamp = texture(sampler_pw_main,uvm + (noiseVal.xy-.5)*12*pixelsize).xyz;
if (length(ret.xy - uvm) > length(rsamp.xy - uvm)) {
ret.xy = rsamp.xy;
}

rsamp = texture(sampler_pw_main,uvm + (noiseVal.zw-.5)*48*pixelsize).xyz;
if (length(ret.xy - uvm) > length(rsamp.xy - uvm)) {
ret.xy = rsamp.xy;
}

ret.z = texture(sampler_fw_main,ret.xy).z;

ret.z -= q1*.005;

if (ret.z < .09) {
ret*=0;
}


}

comp:
uniform sampler2D sampler_fw_sky;
float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body
{
vec3 nv = texture(sampler_noise_lq, uv).xyz;
half1 mod = 0;
//ret.z = 0;

vec2 oset = vec2(2,0)*texsize.zw;
ret.xy = texture(sampler_main, uv+oset).xy;
vec2 rsamp = texture(sampler_main, uv - oset).xy;

vec3 dist = vec4(ret.x,ret.y,rsamp.x,rsamp.y);
dist -= uv.xyxy;
dist *= dist;
dist += dist.yxwz;
dist = _pow(dist,.5);

if (abs(ret.x-rsamp.x) > 0) {
mod = 1;
}

oset = vec2(0,2)*texsize.zw;
ret.xy = texture(sampler_main, uv+oset).xy;
rsamp = texture(sampler_main, uv - oset).xy;

dist = vec4(ret.x,ret.y,rsamp.x,rsamp.y);
dist -= uv.xyxy;
dist *= dist;
dist += dist.yxwz;
dist = _pow(dist,.5);

if (abs(ret.y-rsamp.y) > 0) {
mod = 1;
}


vec2 uvm = uv + texture(sampler_main, uv).xy*100;

uvm = abs(fract((uvm+q12)*.5) - .5)*2 +q10;

uvm = abs(fract((uvm)*.5) - .5)*2;

ret =texture(sampler_fw_sky, uvm).xyz;

mod *= length(ret.xy-uv);

if (mod.x > 0) {
ret =mod.x*.25;
}


}

