sounds=(bass+treb+mid)/3;

rad=rad*sounds*0.6;

r=r+sin(bass)*g;
g=g+sin(mid)*g;


x=x+0.207*cos(time*bass*0.1);
y=y+0.207*sin(time*bass*0.1);

g2=g2+0.09*sin((treb+mid)*1.23);
g=g+0.09*cos((bass+mid)*1.37);

sounds=(bass+mid+treb)/3;

rad=rad*(treb+mid)/3;

x=x+0.300*sin(time*bass);
y=y+0.317*cos(time*bass);

sounds=(bass+treb+mid)/3;
att=(bass_att+treb_att+mid_att)/3;
att=(bass_att+treb_att+mid_att)/3;
r2=r2+(0.25*sin(time*6.28))-bass;
g2=g2+(0.25*sin(time*6.28))-bass;

rad=rad;
x=x+0.217*cos(time*2.3)+sounds*0.1;
