frame_eqs_eel:
dec_med = 1- .2*30/fps;
dec_slow = 1-.05*30/fps;

beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .2*avg+peak) * above (time, t0+.1);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %16;

q27 = index;
q22 = peak;
peak_ = dec_med*peak_ + (1-dec_med)*peak;

trig = is_beat * bnot(index%4);
count = count + trig;
rott = rott*dec_med + (1-dec_med) * count*3.14/2;
q1 = cos(rott);
q2 = sin(rott);


ma = ma + (peak_-0)/fps*4*above(peak_,0);
mx=mx*dec_slow+(1-dec_slow)*cos(ma);
my=my*dec_slow+(1-dec_slow)*sin(ma*q1);
q15 = mx/2 ;
q16 = my/2 ;
q17 = ma;

ang1 = ang1 + 8*bass*above(bass,1)/fps*q1;
q18 = ang1;

q32 = aspecty;
monitor = q1;

warp:
vec3 crisp,dx,dy,ret1;
vec2 zz, uv1,uv2;
float  n1,n2,n3,n4,zv,z, noise, k1, wave;
vec2 sunpos = vec2 (0*sin(time/3),-.0);
mat2 ofs = mat2 (texsize.z,0,0,texsize.w)*4;
vec3 suncol = 1+normalize(slow_roam_cos);
vec3 cloud (vec2 uv_in) {
return (.2+(1+bass_att)*.01/length(uv_in-sunpos)*suncol+(rand_preset.z-.5)*uv_in.y);} 

float _pow(float b,float e){return pow(b,e);}
vec2  _pow(vec2  b,float e){return pow(b,vec2(e));}
vec3  _pow(vec3  b,float e){return pow(b,vec3(e));}
vec4  _pow(vec4  b,float e){return pow(b,vec4(e));}
shader_body {

vec2 uv1 = vec2(uv.x-0.5+q19,uv.y-0.5);//*aspect.xy;

dx = texture(sampler_main,uv+ofs[0]).xyz - texture(sampler_main,uv-ofs[0]).xyz;
dy = texture(sampler_main,uv+ofs[1]).xyz - texture(sampler_main,uv-ofs[1]).xyz;
zz = vec2(dx.b,dy.b);

//clouds
zv = time*.06 ;
zv = time*.08+ .0*texture(sampler_main, uv).xyz; //##
uv2 = 3*uv1+ vec2(0.02*time,-.05*time);

n1 = texture(sampler_noisevol_hq,vec3 (uv2.x,uv2.y,zv)).b;
n2 = texture(sampler_noisevol_hq,vec3 (uv2.x*2,uv2.y*2,zv*2)).b;
n3 = texture(sampler_noisevol_hq,vec3 (uv2.x*4,uv2.y*4,zv*3)).b;
n4 = texture(sampler_noisevol_hq,vec3 (uv2.x*8,uv2.y*8,zv*7)).b;
noise = n1 + n2/2 + n3/4 + n4/8 -1;
noise = _pow(noise,1);
uv2 = uv1+noise*.1;

//*******************************
ret1 = cloud(uv2);
float k1 = .1;
ret =ret1*k1/2 + texture(sampler_main, uv-.02*zz).xyz*(1-k1)-.01;

//ret =vec3(0);
}

comp:
shader_body
{
    ret =texture(sampler_main, uv).xyz;
    //ret += ((texture(sampler_blur1, uv).xyz * scale1) + bias1)*0.7; ret *= 0.6;

