vec3 crisp,dx,dy,ret1;
vec2 zz, uv1,uv2;
float  n1,n2,n3,n4,zv,z, noise, k1, wave;
vec2 sunpos;
mat2 ofs;
vec3 suncol;
vec3 cloud (vec2 uv_in) {
return (.2+(1.0+bass_att)*.01/length(uv_in-sunpos)*suncol+(rand_preset.z-.5)*uv_in.y);} 




shader_body
{
vec2 _uv = uv;

sunpos = vec2 (0.0*sin(time/3.0),-.0);
ofs = mat2 (texsize.z,0.0,0.0,texsize.w)*4.0;
suncol = vec3((1.0+normalize(slow_roam_cos.xy)).x);

vec2 uv1 = vec2(_uv.x-0.5+q19,_uv.y-0.5);//*aspect.xy;

dx = texture(sampler_main,_uv+ofs[0]).xyz - texture(sampler_main,_uv-ofs[0]).xyz;
dy = texture(sampler_main,_uv+ofs[1]).xyz - texture(sampler_main,_uv-ofs[1]).xyz;
zz = vec2(dx.b,dy.b);

//clouds
zv = time*.06 ;
zv = (time*.08+ .0*texture(sampler_main, _uv).xyz).x; //##
uv2 = 3.0*uv1+ vec2(0.02*time,-.05*time);

n1 = texture(sampler_noisevol_hq,vec3 (uv2.x,uv2.y,zv)).b;
n2 = texture(sampler_noisevol_hq,vec3 (uv2.x*2.0,uv2.y*2.0,zv*2.0)).b;
n3 = texture(sampler_noisevol_hq,vec3 (uv2.x*4.0,uv2.y*4.0,zv*3.0)).b;
n4 = texture(sampler_noisevol_hq,vec3 (uv2.x*8.0,uv2.y*8.0,zv*7.0)).b;
noise = n1 + n2/2.0 + n3/4.0 + n4/8.0 -1.0;
noise = pow(noise,1.0);
uv2 = uv1+noise*.1;

//*******************************
ret1 = cloud(uv2);
float k1 = .1;
ret = ret1*k1/2.0 + texture(sampler_main, _uv-.02*zz).xyz*(1.0-k1)-.01;

//ret = 0.0;




}

comp:
vec3 ret1, neu, crisp, blur;
vec2 uv2, uv3;

shader_body
{
float ang2, c, s;
uv -= 0.5;
uv *= aspect.xy;    

vec2 tmp = vec2(uv);
float dist = 1.0;
float inten = 1.0;
float mask = 0.0;
ret1 = vec3(0.0);
float anz = 3.0;
float n = 0.0;
float t_rel = q29;
while (n <= anz) {
    ang2 = 6.28*n/anz+q30;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;
    uv2*= aspect.yx;

    dist = 1.0-fract(n/anz+t_rel); //evtl sqrt !

    inten =  4.0*dist*(1.0-dist*dist);
    uv3 = 3.0*uv2*dist + 0.5+.0*vec2(q5,q6);
    crisp = texture(sampler_main, uv3).xyz;
    blur = ((texture(sampler_blur1, fract(uv3)).xyz * scale1) + bias1) * vec3 (.8,.9,1.0);
    ret1 = max(ret1,(crisp+blur)*inten);
n++;
}


vec3 ret1 = ret1 ;
