shader_body
{
vec2 _uv = uv;

  vec2 uv2 = vec2(mix(uv_orig, _uv, 0.2));

  // diffused version:
  float ss = 0.3;
  vec3 blur = 0.25*(
    texture(sampler_main, uv2+ss*texsize.zw*vec2( 1.0, 1.0)).xyz +
    texture(sampler_main, uv2+ss*texsize.zw*vec2(-1.0, 1.0)).xyz +
    texture(sampler_main, uv2+ss*texsize.zw*vec2( 1.0,-1.0)).xyz +
    texture(sampler_main, uv2+ss*texsize.zw*vec2(-1.0,-1.0)).xyz
  );

  // diffusion:  
  ret = texture(sampler_main,uv2).xyz;//blur;

  float F2 = 0.035;
  float k2 = 0.060;
  float s = 0.025;
  float scale = 9.0;

  // reaction:
  vec3 z = vec3(ret);
  float uvv = ret.x*ret.y*ret.y;
  z.x += (-uvv + F2*(1.0-ret.x) + 0.0007)*scale;
  z.y += (uvv - (F2+k2)*ret.y)*scale;
  //z.x += s*(16.0-ret.x*ret.y);
  //z.y += s*(ret.x*ret.y - ret.y - ret.z);
  z.z -= 0.02;

  // apply laplacian:
  z.x += ((blur - texture(sampler_main, uv2).xyz)*0.2*scale).x;

  ret = z;  
  //ret *= 0.98 + 0.02*clamp( (0.75 - rad )*10.0 , 0.0, 1.0);
  //ret *= 0.99;

  // add noise:
  ret.x += (0.09*(texture(sampler_noise_lq, _uv*texsize.xy*texsize_noise_lq.zw + rand_frame.xy).xyz*2.0-1.0)).x;

  // darken (decay) over time
  //ret = (ret - 0.002)*0.99;



}

