warp:
shader_body
{
vec2 _uv = uv;

    vec2 scale = vec2(1280.0,1024.0)*texsize.zw; // 1280x1024 : the resolution i modelled this shader with
    float d = 0.005;
    float dx = ( ((texture(sampler_blur2, _uv + vec2(d,0.0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, _uv-vec2(d,0.0)).xyz * scale2) + bias2) ).x*scale.x;
    float dy = ( ((texture(sampler_blur2, _uv + vec2(0.0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, _uv-vec2(0.0,d)).xyz * scale2) + bias2) ).x*scale.y;
    float dxb = ( ((texture(sampler_blur2, _uv + vec2(d,0.0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, _uv-vec2(d,0.0)).xyz * scale2) + bias2) ).x*scale.x;
    float dyb = ( ((texture(sampler_blur2, _uv + vec2(0.0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, _uv-vec2(0.0,d)).xyz * scale2) + bias2) ).x*scale.y;
    vec2 my_uv = _uv - vec2(dx,dy)*0.01 + vec2(dxb,dyb)*0.003;



    vec2 v = vec2(0.01);
    ret.x = texture( sampler_fw_main, my_uv -floor(my_uv)*0.0).x;


    ret.x += (ret.x - ((texture(sampler_blur3, _uv).xyz * scale3) + bias3).x)*.1;
    ret.x -= 0.01;


//--------------------------------

    my_uv = _uv + vec2(dy,-dx)*0.05*(1.2-((texture(sampler_blur3, _uv).xyz * scale3) + bias3).y);



    v = vec2(0.01);
    ret.z = texture( sampler_fw_main, my_uv).z;

    ret.z += (ret.z - ((texture(sampler_blur1, _uv).xyz * scale1) + bias1).z)*length(my_uv-_uv)*180.0/length(scale);
    ret.z *= 0.8;
    ret.z -= 0.01;



//--------------------------------

    d = 0.01;
    my_uv = vec2(-dy,dx)*0.045;

    dx = ( ((texture(sampler_blur2, _uv + vec2(d,0.0)).xyz * scale2) + bias2) - ((texture(sampler_blur2, _uv-vec2(d,0.0)).xyz * scale2) + bias2) ).y*scale.x;
    dy = ( ((texture(sampler_blur2, _uv + vec2(0.0,d)).xyz * scale2) + bias2) - ((texture(sampler_blur2, _uv-vec2(0.0,d)).xyz * scale2) + bias2) ).y*scale.y;
    my_uv += _uv - vec2(dx,dy)*0.03;
    v = vec2(0.01);
    ret.y =texture( sampler_fw_main, my_uv).y;
    ret.y += (ret.y - ((texture(sampler_blur3, my_uv).xyz * scale3) + bias3).y)*0.1 - 0.01;





//ret = 0.0;

}

comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz*-1.0 +
              ((texture(sampler_blur1, uv).xyz * scale1) + bias1)*1.0 +
              ((texture(sampler_blur2, uv).xyz * scale2) + bias2)*1.0;
    ret *= 1.5;
}
