shader_body
{
vec2 _uv = uv;

    // sample previous frame
    ret = texture( sampler_fc_main, _uv ).xyz;

    // motion blur by Geiss
    // 'v' points exactly one pixel, in the direction of motion
    vec2 v = normalize(_uv-uv_orig)*texsize.zw;
    vec3 s;
    ret = max(ret, texture(sampler_main, _uv+v).xyz*0.97);
    ret = max(ret, texture(sampler_main, _uv-v).xyz*0.97);
    ret = max(ret, texture(sampler_main, _uv+v*2.0).xyz*0.90);
    ret = max(ret, texture(sampler_main, _uv-v*2.0).xyz*0.90);

    // darken (decay) over time
    ret = (ret-0.005)*0.98; //or try: ret -= 0.004;

}

