warp:
shader_body
{
vec2 _uv = uv;

    // sample previous frame
    ret = texture( sampler_main, _uv ).xyz;
    vec3 ret2 = texture( sampler_fc_main, _uv ).xyz;

    ret.x = texture( sampler_main, (_uv-0.5)*1.00+0.5 ).x;
    ret.y = texture( sampler_main, (_uv-0.5)*0.98+0.5 ).y;
    ret.z = texture( sampler_main, (_uv-0.5)*0.96+0.5 ).z;   

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(((texture(sampler_blur2, _uv).xyz * scale2) + bias2)-.5))*5.0;
    ret = ret - .01;

}

comp:
shader_body
{
vec2 uv1 = (uv-0.5)*aspect.xy;

float rad2 = length(uv1) + .1;
float rad1 = .1/rad2 ;

vec2 uv2 = vec2 (ang/3.14, q27*rad1);
uv2.y = uv2.y  +.1*q30;
uv2.x = uv2.x  +.2*time;

vec2 uv3 = vec2 (ang/3.14, 4.0*rad1);
uv3.y = uv3.y  -.1*q30;


vec3 crisp = 2.0*texture(sampler_main, uv2).xyz + texture(sampler_main, uv3).xyz;
crisp = max(crisp,2.0*((texture(sampler_blur2, fract(uv2)).xyz * scale2) + bias2) + ((texture(sampler_blur2, fract(uv3)).xyz * scale2) + bias2));

vec3 lay1 = vec3 (0.0,0.0,1.0)*uv.y*pow(1.0-rad,8.0);

crisp = 3.0*crisp * pow(rad,1.0);
float mask = clamp(1.0-4.0*rad, 0.0, 1.0);

ret = crisp + lay1*mask + mask * 2.0*((texture(sampler_blur1, uv).xyz * scale1) + bias1);

}
