warp:
shader_body
{
vec2 _uv = uv;

    // PAINTERLY EFFECT:
    vec2 uv2 = vec2(_uv);
    const float mipbias = 0.0;
    vec2 delta2 = texsize.zw*vec2(1.0,1.0);
    vec3 blurry_color = texture( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * 0.01;

    // sample previous frame
    ret = texture( sampler_main, uv2 ).xyz;

    ret += (texture(sampler_noise_lq, uv_orig*4.0 + rand_frame.xy).xyz*2.0-1.0)*0.02;

    float ang2 = atan(uv_orig.y-0.5,uv_orig.x-0.5);
    ret += (  cos(ang2*17.0 + time*12.0 + lum(ret)*0.0) )*0.15;

    // darken over time
    //ret -= 0.004;    

}

comp:
vec2 rs;



shader_body
{
uv = 0.5 + (uv-0.5)*aspect.xy;
rs.x = ang/3.14 + rad*(q15-q16) - q18;
rs.y = .1/(.05+length (uv-0.5)*1.4)+q21;

uv = fract(rs);

vec2 hor = vec2 (texsize.z,0.0)*4.0;
vec2 ver = vec2 (0.0,texsize.w)*4.0;
float dx = (lum(((texture(sampler_blur1, uv-hor).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv+hor).xyz * scale1) + bias1))).x;
float dy = (lum(((texture(sampler_blur1, uv-ver).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv+ver).xyz * scale1) + bias1))).x;
vec2 dz = vec2 (dx,dy);

vec3 ret1 = texture(sampler_main, uv).xyz*4.0;

uv = .4*cos(uv*3.14*4.0 - vec2(0.0,10.0)*(q21+q20)) - 4.0*dz;
vec3 dots = vec3(clamp(.02/length(uv), 0.0, 1.0)*hue_shader);

ret = vec3(pow( vec3(2.0*dots*(ret1+1.0)-0.04), vec3(0.8)));
ret = mix(ret,ret.zxy,q19).zxy;
