warp:
shader_body
{
vec2 _uv = uv;

    // sample previous frame
    ret = texture( sampler_main, _uv ).xyz;
    vec3 ret2 = texture( sampler_fc_main, _uv ).xyz;

    ret.x = texture( sampler_main, (_uv-0.5)*1.00+0.5 ).x;
    ret.y = texture( sampler_main, (_uv-0.5)*0.98+0.5 ).y;
    ret.z = texture( sampler_main, (_uv-0.5)*0.96+0.5 ).z;   

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(((texture(sampler_blur2, _uv).xyz * scale2) + bias2)-.5))*5.0;
    ret = ret - .01;

}

comp:
shader_body
{
    ret = texture(sampler_main, uv).xyz;

    ret *= fract(ret*4.0)*2.0;
    ret += ((texture(sampler_blur1, uv).xyz * scale1) + bias1) * .1;
    ret *= ret; 
}
