warp:
shader_body
{
vec2 _uv = uv;

    // sample previous frame
    ret = texture( sampler_main, _uv ).xyz;

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(((texture(sampler_blur2, _uv).xyz * scale2) + bias2)-.5))*5.0;
    ret = ret - .01;

}

comp:
shader_body
{
vec2 uv1 = (uv-0.5)*aspect.xy;

//float ang1 = atan(uv1.y, uv1.x);
//float rad1 = log(length(uv1)-0.1);

float rad2 = length(uv1) + .1;
float rad1 = .1/rad2 ;

vec2 uv2 = vec2 (ang/3.14, rad1);
uv2.y = uv2.y  +0.1*time;
uv2.x = uv2.x  +.0*time;

vec2 uv3 = vec2 (ang/3.14, rad1*1.5);
uv3.y = uv3.y  +0.08*time ;
uv3.x = uv3.x  + time/32.0;

vec3 crisp = 2.0*texture(sampler_main, uv2).xyz + texture(sampler_main, uv3).xyz;
crisp += 2.0*((texture(sampler_blur2, fract(uv2)).xyz * scale2) + bias2) + 2.0*((texture(sampler_blur2, fract(uv3)).xyz * scale2) + bias2);

vec3 lay1 = vec3 (0.0,0.0,1.0)*uv.y*pow(1.0-rad,8.0);

crisp = 3.0*crisp * pow(rad,1.0);

float mask = clamp(1.0-4.0*rad, 0.0, 1.0);

ret = crisp + lay1*mask + mask * texture(sampler_main, uv).xyz;

}
