shader_body
{
vec2 _uv = uv;

vec2 uv_m = vec2(1.0 - abs( fract( _uv * 0.5 ) * 2.0 - 1.0 )); // Eo.S' mirror code
ret.x = texture( sampler_fc_main, uv_m ).y*0.65;


vec2 d = texsize.zw*6.0;
vec2 uv_z = vec2((uv_orig-0.5)*0.996 + 0.5); // a small dose of zoom-in
vec3 dx = ( ((texture(sampler_blur1, uv_z + vec2(1.0,0.0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv_z-vec2(1.0,0.0)*d).xyz * scale1) + bias1) );
vec3 dy = ( ((texture(sampler_blur1, uv_z + vec2(0.0,1.0)*d).xyz * scale1) + bias1) - ((texture(sampler_blur1, uv_z-vec2(0.0,1.0)*d).xyz * scale1) + bias1) );
uv_z -=vec2(dx.z,dy.z)*texsize.zw*2.0; //shortening of the nebulous matter
ret.z = max((texture(sampler_fc_main,uv_m).x-0.5)*3.0*clamp(1.0-length(uv_orig-0.5)*3.2, 0.0, 1.0), // feeded by the inner of the red channel
             texture(sampler_fc_main,uv_z).z);
ret.z += (texture(sampler_fc_main,uv_z).z - ((texture(sampler_blur1, clamp(uv_z, 0.0, 1.0)).xyz * scale1) + bias1).z)*0.02; // sort of geiss' skin dot code


vec2 uv_y = vec2(mix(_uv,uv_orig,1.0004)); // pushing the glassy stuff a little bit
ret.y =max(texture(sampler_fc_main,uv_y).y ,texture(sampler_fc_main,uv_orig).z)-0.008; // feeded by the green channel and fade-out

ret.z *=1.0+ret.y*0.024;

}

