// tribute to suksma!






shader_body
{
    vec2 uv2;

    vec4 c = vec4(1.0,0.4,0.15,0.1);

    uv2 = uv + vec2(1.0,0.0)*texsize.zw;
    float gx1 = (texture(sampler_main, uv2).xyz*c.x + ((texture(sampler_blur1, uv2).xyz * scale1) + bias1)*c.y + ((texture(sampler_blur2, uv2).xyz * scale2) + bias2)*c.z + ((texture(sampler_blur3, uv2).xyz * scale3) + bias3)*c.w).x;
    uv2 = uv + vec2(-1.0,0.0)*texsize.zw;
    float gx2 = (texture(sampler_main, uv2).xyz*c.x + ((texture(sampler_blur1, uv2).xyz * scale1) + bias1)*c.y + ((texture(sampler_blur2, uv2).xyz * scale2) + bias2)*c.z + ((texture(sampler_blur3, uv2).xyz * scale3) + bias3)*c.w).x;

    uv2 = uv + vec2(0.0,1.0)*texsize.zw;
    float gy1 = (texture(sampler_main, uv2).xyz*c.x + ((texture(sampler_blur1, uv2).xyz * scale1) + bias1)*c.y + ((texture(sampler_blur2, uv2).xyz * scale2) + bias2)*c.z + ((texture(sampler_blur3, uv2).xyz * scale3) + bias3)*c.w).x;
    uv2 = uv + vec2(0.0,-1.0)*texsize.zw;
    float gy2 = (texture(sampler_main, uv2).xyz*c.x + ((texture(sampler_blur1, uv2).xyz * scale1) + bias1)*c.y + ((texture(sampler_blur2, uv2).xyz * scale2) + bias2)*c.z + ((texture(sampler_blur3, uv2).xyz * scale3) + bias3)*c.w).x;

    vec3 g = normalize(vec3(gx1-gx2, gy1-gy2, 0.14));

    ret = 0.5 + 0.5*g;
    ret *= clamp(length(ret.xy-0.5)*5.0, 0.0, 1.0);
    ret = ret.xxy;
    ret += 1.15;
    ret *= mix( vec3(ret),ret*(((texture(sampler_blur3, vec2(uv)).xyz * scale3) + bias3)-((texture(sampler_blur1, vec2(uv)).xyz * scale1) + bias1)*treb_att),pow(hue_shader.xzy, vec3(bass_att)));
    ret *= ret;

    //ret *= 1.5; //old gamma effect    





}
