comp:
// (built-in) uniform sampler2D sampler_pw_noise_lq;
float mask1, mask2, mask3, rad1, rad2, rad3;
vec2 rs0, rs, rs1,dz, rsk, dz1, uv4, 
       Kugel1, Kugel2, Kugel3;
vec3 noise, ret1, ret2;



shader_body
{
vec2 uv1 = (uv-.5 )*aspect.xy;

uv1 = (mat2(_qb) * uv1);

//Kugel1
rsk = (uv1 + vec2 (q11,q12)) ;
dz1 = normalize(rsk);
rad1 = 8.0*(q13)*length (rsk) ;
uv4 =  tan(rad1)*dz1;
mask1 = clamp(10.0-7.0*rad1, 0.0, 1.0);
Kugel1 = uv4*mask1/(1.0+q13*q13)*q13;

//Kugel2
rsk = (uv1 + vec2 (q14,q15)) ;
dz1 = normalize(rsk);
rad2 = 2.0*(1.0+q16)*length (rsk) ;
uv4 =  tan(rad2)*dz1;
mask2 = clamp(10.0-7.0*rad2, 0.0, 1.0);
Kugel2 = uv4*mask2/(1.0+q16*q16)*q16;

//Kugel3
rsk = (uv1 + vec2 (q17,q18)) ;
dz1 = normalize(rsk);
rad3 = 4.0*(1.0+q19)*length (rsk) ;
uv4 =  tan(rad3)*dz1;
mask3 = clamp(10.0-7.0*rad3, 0.0, 1.0);
Kugel3 = uv4*mask3/(1.0+q19*q19)*q19;

float z = .1/abs(uv1.y*rad*rad);
rs0.y =  z ;
rs0.x = uv1.x*z*4.0;

rs0 = (mat2(_qa) * rs0);
rs = rs0 + vec2 (0.0,q30);
ret2 = ((texture(sampler_blur1, fract(rs)).xyz * scale1) + bias1)/z;

uv1 += (Kugel1 + Kugel2 + Kugel3)*.25 + ret2.xy/4.0;
ret1 = ret2 + (texture(sampler_main, vec2(uv1/2.0+.5)).xyz) + .4/rad1 * (.5-((texture(sampler_blur1, vec2(0.1)).xyz * scale1) + bias1))*q22*(mod(q27,4.0)) + .3/rad2 * (.5-((texture(sampler_blur1, vec2(0.2)).xyz * scale1) + bias1))*q22*(mod((q27+1.0),4.0)) + .2/rad3 * (.5-((texture(sampler_blur1, vec2(0.0)).xyz * scale1) + bias1))*q22*(mod((q27+2.0),4.0));

ret = clamp(ret1*(1.0+ret1), 0.0, 1.0);


}
