warp:
shader_body
{
vec2 _uv = uv;

    // sample previous frame
    ret = texture( sampler_fc_main, _uv ).xyz;

    // darken (decay) over time
    ret -= 0.002;

}

comp:
shader_body
{
    uv = 0.05 + 0.9*uv;
    ret = texture(sampler_main, uv).xyz;
      // SUPER GLOW EDGES - looks awesome w/octopus
      vec3 avg_col = ((texture(sampler_blur1, uv).xyz * scale1) + bias1);
      ret = abs(avg_col - ret)*6.0;
    ret *= 1.333; // a little bit of overbright
}
