warp:
shader_body
{
vec2 _uv = uv;


    vec3 txr = vec3(_uv,q2*10.0);

    float final_noise = 0.0;

    //perlin noise
    final_noise += (((1.0/pow(2.0,0.0))*((texture(sampler_noisevol_lq, txr*pow(2.0,0.0)).xyz*2.0)-1.0))).x; 
    final_noise += (((1.0/pow(2.0,1.0))*((texture(sampler_noisevol_lq, txr*pow(2.0,1.0)).xyz*2.0)-1.0))).x; 
    final_noise += (((1.0/pow(2.0,2.0))*((texture(sampler_noisevol_lq, txr*pow(2.0,2.0)).xyz*2.0)-1.0))).x; 
    final_noise += (((1.0/pow(2.0,3.0))*((texture(sampler_noisevol_lq, txr*pow(2.0,3.0)).xyz*2.0)-1.0))).x; 


    final_noise =  (final_noise+1.0)*0.5;


    ret = texture( sampler_main, (_uv-0.5)*1.5*final_noise + 0.5 ).xyz; 

    // darken (decay) over time
    ret = ret; //or try: ret -= 0.004;





}

comp:
shader_body
{

    vec2 uv_echo = (uv - 0.5)*1.000*vec2(-1.0,-1.0) + 0.5;
    ret = mix( texture(sampler_main, uv).xyz, 
                texture(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret *= 2.00; //gamma
    vec3 retish = vec3(1.0 - ret*(1.0-ret)*4.0); //solarize
    ret = 1.0 - retish;
}
