warp:
shader_body
{
vec2 _uv = uv;

    // sample previous frame
    ret = texture( sampler_fw_main, _uv ).xyz;

    // darken (decay) over time
    ret -= 0.00;

}

comp:
shader_body
{
vec4 mb = vec4(0.0);
mb.x+=.1*time;

mb.x = (texture(sampler_main, (((texture(sampler_blur1, uv).xyz * scale1) + bias1)).xy).xyz).x;
mb.y = (((texture(sampler_blur1, uv).xyz * scale1) + bias1)).x;
mb.z = (((texture(sampler_blur2, uv).xyz * scale2) + bias2)).x;
mb.w = (((texture(sampler_blur3, uv).xyz * scale3) + bias3)).x;

ret=mb.x;

    vec2 uv_echo = (uv - .5)*1.0*vec2(-1.0,-1.0) + .5;
    ret += mix( texture(sampler_fw_main, uv).xyz*5.0, 
                texture(sampler_fw_main, uv_echo).xyz*5.0, 
                .5 
              ); //video echo

ret = ret*(1.0-ret); //solarize
ret.x*=ret.x*mb.y;
ret.y*=ret.y*mb.y;
ret.z*=1.0-(ret.z/ret.x)+1.0;

vec3 aret = vec3((1.0-ret));
aret.x=1.0/ret.x;
aret.y*=1.0/ret.y;
aret.z-=1.0/ret.z;
ret=ret/aret*mb.w;

;





}
