warp:
shader_body
{
vec2 _uv = uv;

    // sample previous frame
    ret = texture( sampler_fc_main, _uv ).xyz*.8;
    ret += ((texture(sampler_blur1, _uv).xyz * scale1) + bias1)*.2;

    // darken (decay) over time and distance from center
    ret *= ret/(1.4*rad); //or try: ret -= 0.004;

}

comp:
vec3 ret1, neu, blur;


shader_body
{
vec2 uv2;
float ang2, c, s;
uv -= 0.5;
uv *= aspect.xy;    

vec2 tmp = vec2(uv);
float dist = 1.0;
float dista = 0.0;
float inten = 1.0;
float mask = 0.0;
ret1 = vec3(0.0);
float anz = 4.0;
float n = 1.0;
//uv = (mat2(_qa) * uv);
while (n <= anz) {
    ang2 = 6.28*n/anz;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;

    dist = 1.0-fract(.25*n+time/1.0); //evtl sqrt !
    inten =  pow(dist,.3)*(1.0-dist)*2.0;
//    inten =  (1.0-dist);

    neu =  texture(sampler_main, 2.0*uv2*dist + 0.5 ).xyz;
    blur = ((texture(sampler_blur1, 2.0*uv2*dist + 0.54 ).xyz * scale1) + bias1);
    neu += blur;
//    neu *= (neu >= 0.0);
  //  mask = 1.0-(neu*inten >= .1) * (dist < dista);
    ret1 = ret1*.9+neu*inten;
    dista = dist;
n++;
}

float dz = (ret1/2.0).x;
vec2 rs1 = vec2(uv*8.0 + time);

float scale = 64.0;
rs1 = rs1 + dz*scale*.4;

float lava = texture (sampler_noise_hq,rs1/scale).x;
lava = clamp(-abs (lava) + 1.0, 0.0, 1.0);
vec3 lay1 = clamp(vec3 (lava,lava*(lava-0.55)*2.0,(lava-1.0)*16.0), 0.0, 1.0);

ret = ret1*1.0 + 0.0*vec3(0.0,0.0,.3)*rad + lay1*0.0;


}
