vec2 mov;

float hq_n (vec3 uvi) {return (lum(texture (sampler_noise_hq ,uvi.xy).xyz)).x;}
float noise2 (vec3 uvi) {return (texture(sampler_noise_lq, vec2(uvi*8.0)).xyz).x;}

float noise4 (vec2 uvi) { float n1,n2,n3,n4,zv; zv = 0.006*time;
  n1 = (lum(texture(sampler_noise_hq, (uvi)).xyz)).x;
  n2 = (lum(texture(sampler_noise_hq, (uvi*2.0+zv)).xyz)).x;
  n3 = lum(texture(sampler_noise_hq, (uvi*4.0+zv)).xyz);
  n4 = lum(texture(sampler_noise_hq, (uvi*8.0+4.0*zv)).xyz);
  return n1 + n2/2.0 + n3/4.0 + n4/8.0;}

float noise3 (vec3 uvi) {float arg;
return  clamp(texture (sampler_noise_hq,uvi.xy/16.0).xyz-.5,-.25,.25)*2.0 +(texture (sampler_noise_hq,uvi.xy/8.0).xyz-.5)/4.0 +(texture (sampler_noise_hq,(uvi.x-sin(uvi.y*2.0))/2.0).xyz)/12.0;}

vec3 rs_lav (vec2 uvi, float berg) {  float z; vec2 rs;
  z = 8.0/(uvi.y)*(_qb.w-4.0*berg);
  z = clamp(z,-8.0,80.0*hordist);
  //#final check !
  rs.x = uvi.x*z ;
  rs.y = z;
  rs = (mat2(_qa.z,_qa.w,-_qa.w,_qa.z) * rs);  
  return vec3(rs/64.0 + mov,z); } 

vec3 rs_sky (vec2 uvi, float berg) {  float z; vec2 rs;
  z = 8.0/(uvi.y - berg-.5);
  z = clamp(z,-32.0,1.0);
  rs.x = (uvi.x)*z ;
  rs.y = z;
  rs = (mat2(_qa.z,_qa.w,-_qa.w,_qa.z) * rs);
  return vec3(-rs/64.0+mov/-128.0,z); }






shader_body
{
vec2 _uv = uv;

quality = 4.0;
depth = 3.5;
lav_gnd = 0.0;
hordist = 8.0;
tele = 1.0;
mov = vec2 (_qb.x,_qb.y);
uv1 = _uv*aspect.xy-vec2(.5,q7);
uv1 = (mat2(q1,q2,-q2,q1) * uv1);
screen3 = vec3 (vec2(_uv.x*2.0-1.0,_uv.y-.5)*aspect.yx,1.0);

//CLOUDS
uv2 = rs_sky(uv1,0.0);
tmp = 0.0;
for (float n = 1.0; n <= 3.0; n++) {
  uv2 = rs_sky(uv1,tmp*.03);
  tmp = clamp(lum(texture (sampler_noise_hq,uv2 +.03*noise4(uv2*2.0)).xyz-.5), 0.0, 1.0);}
tmp = clamp(pow(tmp,1.4) * sign(tmp), 0.0, 1.0);
cloud = tmp;
ncloud = clamp(1.0-tmp, 0.0, 1.0);

//DUNES
for (n=1.0; n <= quality; n++) {
  rsl = vec3(rs_lav(tele*uv1,lav_gnd)); //#spiele an _uv-Vorfakt - zoom, auch bei sky !
  lprof = noise3(rsl);
  lav_gnd -= lprof/quality*depth/sqrt(rsl.z); } 
rsl = vec3(rs_lav(tele*uv1,lav_gnd)); 
stone = noise2(rsl);

vec3 rsl3 = rsl + vec3(-.1,.0,0.0);
lprof2 = noise3(rsl3);
stone_lit = .3+4.0*clamp(.2+lprof-lprof2, 0.0, 1.0);

//------------------- masking -----------------------------------------
lavmask = clamp(4.0*hordist-rsl.z/18.0, 0.0, 1.0) * (rsl.z >= 0.0); // # !
ilavmask = clamp(1.0-lavmask, 0.0, 1.0);

//---------------- skylight first-----------------
ret1 = ncloud*_uv.y*vec3 (.3,.4,1.0); 
ret1 += cloud*2.0;
ret1 *= ilavmask;  

//----------------lava / stone  -----------------
ret1 += stone *stone_lit*lavmask * vec3(1.0,.4,.0);


ret = 1.0-exp(-ret1*2.0+.1+.1*sin(time/7.0)); 

vec4 noise9 = texture(sampler_noisevol_hq, 
                      ((vec3(uv1.y-uv2.x,uv2.x+uv1.x,_uv.x*_uv.y)*q27 )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      vec3(1.0,1.0,0.0)*0.05 + 
                      time*vec3(0.0,0.0,1.0)*q29
                     ).xyz;
if(ret.x> q21*roam_cos.y && ret.x<= q24*roam_sin.x)ret.y -= (noise9).x*.5; 
if(ret.y> q22*roam_sin.x && ret.y<= q25*roam_cos.x)ret.z -= (noise9).y*.5; 
if(ret.z> q23*roam_cos.x && ret.z<= q26*roam_sin.y)ret.x -= (noise9).z*.5; 






}

