warp:
shader_body
{
vec2 _uv = uv;

    // sample previous frame
    ret = texture( sampler_fw_main, _uv ).xyz;
    // darken (decay) over time
    ret = ret-.04;

}

comp:
shader_body
{

//Code bits by Flexi and Geiss

    vec2 d = texsize.zw*8.0;
    float dx = ( ((texture(sampler_blur1, uv+vec2(1.0,0.0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(1.0,0.0)*d).xyz * scale1) + bias1) ).y;
    float dy = ( ((texture(sampler_blur1, uv+vec2(0.0,1.0)*d).xyz * scale1) + bias1)-((texture(sampler_blur1, uv-vec2(0.0,1.0)*d).xyz * scale1) + bias1) ).y;
    vec2 uv_y = uv+vec2(dx,dy)*0.55;  
    vec2 uv_y2 = (uv_y*1.25)-.125;  


    ret = mix(ret,1.0-ret,.7*((texture(sampler_blur1, uv_y).xyz * scale1) + bias1)+.2*texture(sampler_main, uv).xyz);
    ret = mix(ret,ret-.1*((texture(sampler_blur3, uv_y).xyz * scale3) + bias3)+.1*((texture(sampler_blur1, uv_y2).xyz * scale1) + bias1)+.42*texture(sampler_main, uv_y).xyz,.25);
    //ret *= .75;
    ret *= clamp(length(ret-0.5)*5.0, 0.0, 1.0);
    //ret = ret.xy;
    ret = lum(ret);
    ret += 1.25;
    ret *= ret*.45;
    vec3 mask = ((texture(sampler_blur3, uv).xyz * scale3) + bias3)-((texture(sampler_blur1, uv).xyz * scale1) + bias1)-.25*((texture(sampler_blur2, uv_y).xyz * scale2) + bias2);
    ret *= mix(.9*ret,1.2*ret*mask,pow(hue_shader.zxy,ret));
    ret += .02;
    ret *= ret;
    ret *= hue_shader.zxy;
    ret *= (2.0 + max(treb_att,mid_att));

}
