shader_body
{
vec2 _uv = uv;


ret = 1.25*((texture(sampler_blur3, _uv).xyz * scale3) + bias3)-.25*((texture(sampler_blur1, _uv).xyz * scale1) + bias1);

_uv *= ((1.0+.15*tan(1.33*((texture(sampler_blur1, clamp(ret.xy, 0.0, 1.0)).xyz * scale1) + bias1)))).x;
_uv -= (.075*tan(1.33*((texture(sampler_blur1, clamp(ret.xy, 0.0, 1.0)).xyz * scale1) + bias1))).x;


    // sample previous frame
    ret = texture( sampler_main, _uv ).xyz;

    // feather pen
    ret = max(ret, texture( sampler_main, (_uv-0.5)*(1.0-8.0*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, texture( sampler_main, (_uv-0.5)*(1.0+8.0*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.024;



